vertex.source:
//vertex position in world coordinates
attribute vec3 vertex_worldSpace;
//surface normal at the vertex in world coordinates
attribute vec3 normal_worldSpace;
//texture coordinates at that vertex
attribute vec2 textureCoordinate_input;

//model Matrix (Identity in our case)
uniform mat4 mMatrix;
//view Matrix
uniform mat4 vMatrix;
//projection Matrix
uniform mat4 pMatrix;

uniform float alpha;
uniform float phi;

//main program for each vertex
void main() {




mat4 M1 = mat4(
vec4(cos(alpha)*(pow(sin(phi),2.0))+pow(cos(phi),2.0), (1.0-cos(alpha))*cos(phi)*sin(phi), -1.0*sin(alpha)*sin(phi), 0.0),
vec4((1.0-cos(alpha))*cos(phi)*sin(phi), cos(alpha)*pow(cos(phi),2.0)+pow(sin(phi),2.0), sin(alpha)*cos(phi), 0.0),
vec4(sin(alpha)*sin(phi), -1.0*sin(alpha)*cos(phi), cos(alpha), 0),
vec4(0, 0, 0, 1)
);
  vec4 vertex_camSpace = vMatrix *M1*  mMatrix * vec4(vertex_worldSpace, 1.0);
  gl_Position = pMatrix * vertex_camSpace;


}

fragment.source:
//for better performance less precision
precision mediump float;

//main program for each fragment = pixel candidate
void main() {
  gl_FragColor = vec4(1.0, 0.0, 0.0, 1.0);
}

vertex.source:
//vertex coordinates in world space for the render quad
attribute vec3 vertex_worldSpace;
//texture coordinate for this vertex and the render quad
attribute vec2 textureCoordinate_input;

//texture coordinate needs to be passed on to the R2T fragment shader
varying vec2 varyingTextureCoordinate;

//main program for each vertex of the render quad
void main() {
  gl_Position = vec4(vertex_worldSpace, 1.0);
  varyingTextureCoordinate = textureCoordinate_input;
}

fragment.source:
precision mediump float;

//a texture sampling unit, which is bound to the render quad texture buffer
uniform sampler2D textureRendered;

//texture coordinates coming from the vertex shader, interpolated through the rasterizer
varying vec2 varyingTextureCoordinate;

//main program for each fragment of the render quad
void main() {
  gl_FragColor = texture2D(textureRendered, varyingTextureCoordinate.st);
}
